import React, { useState } from "react";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
import signin from "../image/signin.jpg";
import { useNavigate } from "react-router-dom";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import Typography from '@mui/material/Typography';
import { grey } from "@mui/material/colors";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 800,
  bgcolor: "background.paper",
  // bgcolor : grey,
  boxShadow: 24,
  p: 4,
  borderRadius: "8px",
};

function Signin({ open, handleClose }) {
  const [value, setValue] = useState("");
  const [otp, setOtp] = useState(""); // OTP state
  const [isOtpSent, setIsOtpSent] = useState(false); // To track if OTP has been sent
  const navigate = useNavigate();

  // Function to simulate sending OTP
  const handleSendOtp = () => {
    if (value) {
      alert(`OTP sent to ${value}. Use 1234 to verify.`);
      setIsOtpSent(true); // Set flag to indicate OTP has been sent
    } else {
      alert("Please enter a valid phone number.");
    }
  };

  // Function to handle OTP submission
  const handleVerifyOtp = () => {
    if (otp === "1234") {
      alert("OTP verified successfully!");
      navigate("/");
    } else {
      alert("Invalid OTP. Please try again.");
    }
  };

  return (
    <>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-signin-title"
        aria-describedby="modal-signin-description"
      >

          <Box sx={style}>
          <Typography className="text-xl font-semibold mt-6 text-center" variant="h4">
              Login or sign up to continue
          </Typography> 
          <Typography className="text-center" >
              scan QR code or enter phone number to login
          </Typography> 
        {/* <Box
          sx={{
            display: "flex",
            position: "absolute",
            top: "50%",
            left: "50%",
            bgcolor: 'gray',
            transform: "translate(-50%, -50%)",
            width: 900,
            margin: "auto",
            bgcolor: "background.paper",
            boxShadow: 24,
            p: 0,
            borderRadius: "12px",
          }}
        > */}
        <Typography id="modal-modal-description" sx={{ display: 'flex' }}>
          {/* <Box
            sx={{
              flex: 1,
              // height: "80%",
            }}
          > */}
          <Typography id="modal-modal-title" sx={{height: "180%" ,alignContent:"center"}} >
            <img src={signin} alt="Signin" />
            </Typography>
          {/* </Box>  */}

          {/* <Box
            sx={{
              flex: 1,
              padding: "20px",
              // display: "flex",
              // flexDirection: "column",
              // justifyContent: "center",
            }}
          > */}
           <Typography sx={{  
              padding: "20px", textAlign: "left", marginLeft : "12%"}}>
            {/* <h1 className="text-xl font-semibold mt-6 text-center">
              Login or sign up to continue
            </h1> */}
            <p className="text-gray-500">Enter the phone number to login</p>
            <PhoneInput
              className="mt-4 w-64 mb-3"
              defaultCountry="IN"
              placeholder="Enter phone number"
              value={value}
              onChange={setValue}
            />
            <p className="text-gray-500">
              By proceeding you confirm that you are above 18 years of age and
              agree to the{" "}
              <span className="text-blue-500">
                Privacy policy & Terms of Use
              </span>
            </p>

            <button
              className="bg-blue-700 w-64 hover:bg-blue-800 text-white font-bold py-2 px-4 rounded mt-4"
              onClick={handleSendOtp}
            >
              Get OTP
            </button>

            {isOtpSent && (
              <div className="mt-6">
                <h2 className="text-gray-500">Enter OTP:</h2>
                <input
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className="bg-gray-100 border border-gray-500 w-64 p-2"
                  placeholder="Enter OTP"
                />
                <button
                  onClick={handleVerifyOtp}
                  className="bg-green-700 w-64 hover:bg-green-800 text-white font-bold py-2 px-4 rounded mt-4"
                >
                  Verify OTP
                </button>
              </div>
            )}
            </Typography>
          {/* </Box> */}
        {/* </Box> */}
        </Typography>
        </Box>
      </Modal>
    </>
  );
}

export default Signin;
