import React, { useState } from "react";
import { Link } from "react-router-dom";
import ProfileOutlinedIcon from "@mui/icons-material/PersonOutline";
import ProfileFilledIcon from "@mui/icons-material/Person";
import SearchOutlinedIcon from "@mui/icons-material/SearchOutlined";
import SearchFilledIcon from "@mui/icons-material/Search";
import HomeOutlinedIcon from "@mui/icons-material/HomeOutlined";
import HomeFilledIcon from "@mui/icons-material/Home";
import MoviesOutlinedIcon from "@mui/icons-material/MovieOutlined";
import MoviesFilledIcon from "@mui/icons-material/Movie";
import LiveTvOutlinedIcon from "@mui/icons-material/LiveTvOutlined";
import LiveTvFilledIcon from "@mui/icons-material/LiveTv";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import Fade from "@mui/material/Fade";
import hostaicon from '../image/dis.png'

const Sidebar = () => {
  const [touch, setTouch] = useState(false);
  const [hoveredIcon, setHoveredIcon] = useState(null);

  const handleMouseEnter = (iconName) => {
    setHoveredIcon(iconName);
    setTouch(true);
  };

  const handleMouseLeave = () => {
    setHoveredIcon(null);
    setTouch(false);
  };

  return (
    <div className="z-20 fixed grid grid-cols-2 bg-black h-full w-28">
      <div onMouseEnter={() => setTouch(true)} onMouseLeave={() => setTouch(false)}>
        <img src={hostaicon} className="ml-7 mt-5 w-40" />
        <div className="w-64 ml-5 mt-1 ">
          <span className="rounded-xl text-center" style={{ backgroundColor: "rgb(74, 62, 30, 1)" }}>
            <span className="text-amber-200 ml-2 text-xs">
              Subscribe <ArrowForwardIosIcon sx={{ width: "0.7rem", marginRight: "4px" }} />
            </span>
          </span>
        </div>

        <div className="mt-52">
          {/* Profile Link */}
          <Link to="/login" className="flex flex-col items-center" onMouseEnter={() => handleMouseEnter("profile")} onMouseLeave={handleMouseLeave}>
            {hoveredIcon === "profile" ? (
              <ProfileFilledIcon className="w-5 cursor-pointer text-slate-100" />
            ) : (
              <ProfileOutlinedIcon className="w-5 cursor-pointer text-slate-300 hover:text-gray-100" />
            )}
            <span className={`mt-2 ${hoveredIcon === "profile" ? "text-white font-bold" : "text-slate-300"}`}>My Space</span>
          </Link>

          {/* Search Link */}
          <Link to="/search" className="flex flex-col items-center mt-12" onMouseEnter={() => handleMouseEnter("search")} onMouseLeave={handleMouseLeave}>
            {hoveredIcon === "search" ? (
              <SearchFilledIcon className="w-5 cursor-pointer text-slate-100" />
            ) : (
              <SearchOutlinedIcon className="w-5 cursor-pointer text-slate-300 hover:text-gray-100" />
            )}
            <span className={`mt-2 ${hoveredIcon === "search" ? "text-white font-bold" : "text-slate-300"}`}>Search</span>
          </Link>

          {/* Home Link */}
          <Link to="/welcome" className="flex flex-col items-center mt-12" onMouseEnter={() => handleMouseEnter("home")} onMouseLeave={handleMouseLeave}>
            {hoveredIcon === "home" ? (
              <HomeFilledIcon className="w-5 cursor-pointer text-slate-100" />
            ) : (
              <HomeOutlinedIcon className="w-5 cursor-pointer text-slate-300 hover:text-gray-100" />
            )}
            <span className={`mt-2 ${hoveredIcon === "home" ? "text-white font-bold" : "text-slate-300"}`}>Home</span>
          </Link>

          {/* Movies Link */}
          <Link to="/movie" className="flex flex-col items-center mt-12" onMouseEnter={() => handleMouseEnter("movie")} onMouseLeave={handleMouseLeave}>
            {hoveredIcon === "movie" ? (
              <MoviesFilledIcon className="w-5 cursor-pointer text-slate-100" />
            ) : (
              <MoviesOutlinedIcon className="w-5 cursor-pointer text-slate-300 hover:text-gray-100" />
            )}
            <span className={`mt-2 ${hoveredIcon === "movie" ? "text-white font-bold" : "text-slate-300"}`}>Movies</span>
          </Link>

          {/* TV Link */}
          <Link to="/tv" className="flex flex-col items-center mt-12" onMouseEnter={() => handleMouseEnter("tv")} onMouseLeave={handleMouseLeave}>
            {hoveredIcon === "tv" ? (
              <LiveTvFilledIcon className="w-5 cursor-pointer text-slate-100" />
            ) : (
              <LiveTvOutlinedIcon className="w-5 cursor-pointer text-slate-300 hover:text-gray-100" />
            )}
            <span className={`mt-2 ${hoveredIcon === "tv" ? "text-white font-bold" : "text-slate-300"}`}>TV</span>
          </Link>
        </div>
      </div>

      {touch && (
        <Fade in={touch}>
          <div className="text-slate-100 bg-black-900 bg-opacity-60 ml-8 w-20 h-screen font-bold text-base">
            <h4 style={{ marginTop: "37.1vh" }} className="hover:font-bold hover:text-gray-400">
              My space
            </h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">Search</h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">Home</h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">Movie</h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">TV</h4>
          </div>
        </Fade>
      )}
    </div>
  );
};

export default Sidebar;








