import React, { useEffect, useState } from "react";

function Home (){

  const [movies, setMovies] = useState([])

    const getmovies = async() => {
        try{
            fetch("https://api.themoviedb.org/3/discover/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75")
            .then(res => res.json())
            .then(json => setMovies(json.results))
        }catch(err){
          console.error(err)
        }
    }

    console.log(movies)

    useEffect(()=>{
        getmovies();
    },[])

    return (

<div className="grid grid-cols-7">
{movies.map((data)=>{
    return  <>
    <div className=" rounded overflow-hidden shadow-lg mt-4 ml-4">
  <img className="w-full" src={`https://image.tmdb.org/t/p/w500${data.poster_path}`} alt="Sunset in the mountains"/>
</div>
</>
  })}
</div>

)
}

export default Home;
