import React from "react";
import { useOutletContext } from "react-router-dom";

function Home ({search}){
  const [movies] = useOutletContext();  // This should always give an array now

   return (
<>
 {search && <input type="search" className="block w-full p-4 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 " placeholder="movies, shows and more" required />}
<div className="grid grid-cols-6 bg-black">
{movies.map((data)=>{
    return  <>
    <div key={data.id} className=" rounded overflow-hidden shadow-lg mt-4 ml-4">
  <img className="w-full" src={`https://image.tmdb.org/t/p/w500${data.poster_path}`} alt="Sunset in the mountains"/>
</div>
</>
  })}
</div>
</>
)
}

export default Home;





