import React, { useState } from "react";
// import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
// import signin from "../image/signin.jpg";
import signin from "../image/qr.png";
import { useNavigate } from "react-router-dom";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import Typography from "@mui/material/Typography";
import { colors } from "@mui/material";
// import Grid from "@mui/material/Grid";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 1000,
  bgcolor: "#16181f",
  color : "white",
  // bgcolor: "background.paper",
  // bgcolor : grey,
  boxShadow: 24,
  p: 4,
  borderRadius: "8px",
};

function Signin({ open, handleClose }) {
  const [value, setValue] = useState("");
  const [otp, setOtp] = useState(""); // OTP state
  const [isOtpSent, setIsOtpSent] = useState(false); // To track if OTP has been sent
  const navigate = useNavigate();

  // Function to simulate sending OTP
  const handleSendOtp = () => {
    if (value) {
      alert(`OTP sent to ${value}. Use 1234 to verify.`);
      setIsOtpSent(true); // Set flag to indicate OTP has been sent
    } else {
      alert("Please enter a valid phone number.");
    }
  };

  // Function to handle OTP submission
  const handleVerifyOtp = () => {
    if (otp === "1234") {
      alert("OTP verified successfully!");
      navigate("/");
    } else {
      alert("Invalid OTP. Please try again.");
    }
  };

  return (
    <>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-signin-title"
        aria-describedby="modal-signin-description"
      >
        <Box sx={style}>
          <Typography variant="h4" align="center" sx={{ mb: 2 }}>
            Login or Sign Up to Continue
          </Typography>
          <Typography align="center" sx={{ mb: 4 , color: "#8f98b2"}}>
            Scan the QR code or enter your phone number to login
          </Typography>

          <Box
            sx={{ display: "flex", flexDirection: { xs: "column", md: "row" } }}
          >
            {/* Left Side: formate */}
            <Box sx={{ flex: 1, textAlign: "center", p: 2 }}>
              <img src={signin} alt="Signin" className="h-52 w-52 ml-24" />
              <Typography variant="h6" sx={{ mt: 2 }}>
                Use your camera app to scan the QR
              </Typography>
              <Typography sx={{ maxWidth: "80%", margin: "auto", mt: 1 }}>
                Click the link generated to redirect to the Disney+ Hotstar
                mobile app
              </Typography>
            </Box>

            {/* Right Side: text formate */}
            <Box sx={{ flex: 1, p: 2, marginLeft: "10%" }}>
              <Typography sx={{ mb: 2 }}>
                Enter your phone number to login
              </Typography>
              <div class="flex items-center w-80">
                <button
                  class="flex-shrink-0 z-10 inline-flex items-center py-2.5 px-4 text-sm font-medium 
    text-center text-gray-900 bg-gray-100 border border-gray-300 rounded-s-lg hover:bg-gray-200 focus:ring-4 focus:outline-none focus:ring-gray-100
     dark:bg-gray-700 dark:hover:bg-gray-600 dark:focus:ring-gray-700 dark:text-white dark:border-gray-600"
                  type="button"
                >
                  +91{" "}
                  <svg
                    class="w-2.5 h-2.5 ms-2.5"
                    aria-hidden="true"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 10 6"
                  ></svg>
                </button>
                <input
                  value={value}
                  onChange={(e) => setValue(e.target.value)}
                  type="text"
                  id="phone-input"
                  class="block p-2.5 w-full z-20 text-sm text-gray-900 bg-gray-50 rounded-e-lg border-s-0 border border-gray-300
         focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-s-gray-700  dark:border-gray-600 dark:placeholder-gray-400 dark:text-white
          dark:focus:border-blue-500"
                  pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                  placeholder="Phone Number"
                  required
                />
              </div>
              <Typography
                sx={{ mt: 2, mb: 2, color: "gray", fontSize: "0.875rem" , maxWidth: "80%" }}
              >
                By proceeding, you confirm that you are above 18 years of age
                and agree to the{" "}
                <a
                  href="https://www.hotstar.com/privacy-policy/in"
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ color: "blue" }}
                >
                  Privacy Policy & Terms of Use
                </a>
                .
              </Typography>

              <Button
                variant="contained"
                color="primary"
                sx={{ width: "80%", mb: 2 }}
                // sx={{ width: '100%' }}
                onClick={handleSendOtp}
              >
                Get OTP
              </Button>

              <Box>
                <Typography>
                  Having trouble logging in? {""}{" "}
                  <a
                    href="https://help.hotstar.com/in/en/support/search?term=login"
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{ color: "blue" }}
                  >
                    Get Help
                  </a>
                  .
                </Typography>
              </Box>

              {isOtpSent && (
                <Box sx={{ mt: 4 }}>
                  <Typography>Enter OTP:</Typography>
                  <input
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    style={{ width: "100%", padding: "8px", marginTop: "8px" }}
                    placeholder="Enter OTP"
                  />
                  <Button
                    variant="contained"
                    color="success"
                    sx={{ mt: 2, width: "100%" }}
                    onClick={handleVerifyOtp}
                  >
                    Verify OTP
                  </Button>
                </Box>
              )}
            </Box>
          </Box>
        </Box>
      </Modal>
    </>
  );
}

export default Signin;
