import React, { useState } from "react";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import { useNavigate } from "react-router-dom";
import Sidebar from "./sidebar";
import loginin from "../image/login_in.png"
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';

function Login() {

  const [openSigninModal, setOpenSigninModal] = useState(false);

  // Function to open the Signin modal
  const handleLoginClick = () => {
    setOpenSigninModal(true);  // This will trigger the modal to be opened
  };

  // Function to close the Signin modal
  const handleCloseModal = () => {
    setOpenSigninModal(false);  // This will trigger the modal to be closed
  };

    // const navigate = useNavigate();

    // const handleLoginClick = () => {
    //     navigate("/signin"); // Navigate to the /signin route
    //   };

  return (
    <>
<Box sx={{padding:"14px 16px"}}>
{/* font:"14px inter , sans-self" */}
  <Button  sx={{background : "#FFFFFF14" ,color: "#FFFFFF", marginLeft:"80%", marginTop: "3%"}}><HelpOutlineIcon/> <span className="ml-3">Help & Support</span></Button>
</Box> 
    <Box >
   <img src={loginin} style={{height : "10%", width: "20%", marginTop:"10%", marginLeft : "40%"}} />
      <Box
        sx={{
          height: "100vh",
          // display: "flex",
          // justifyContent: "center",
          // alignItems: "center",
          textAlign: "center",
          background : "black",
          marginTop : "3%",
          color : "white",
          width : "w-11/12"
        }}
      >
        <Box>
          <Typography variant="h5" gutterBottom>
            Login to Disney+ Hotstar
          </Typography>
          <Typography variant="body1" gutterBottom>
            Start watching from where you left off, personalized for kids, and
            more
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={handleLoginClick}
            sx={{
              width: "18rem", 
              fontWeight: "bold",
              py: 2, 
              px: 4,
              mt: 6, 
              backgroundColor: "#095ae5",
              "&:hover": {
                backgroundColor: "darkblue",
              },
            }}
          >
            Login
          </Button>
          {openSigninModal && (
            <Signin open={openSigninModal} handleClose={handleCloseModal} />
          )}
        </Box>
      </Box>
      </Box>
    </>
  );
}
export default Login;
