import React from "react";
import Sidebar from "./sidebar";
import { Outlet } from "react-router-dom";

function Layout({ search, setSearch, setMenu }) {
  return (
    <div className="bg-black min-h-screen flex">
      {/* Sidebar will always be visible */}
      <div className="w-1/12 bg-black">
        <Sidebar setSearch={setSearch} search={search} setMenu={setMenu} />
      </div>

      {/* This Outlet will render the current route component */}
      <div className="w-11/12">
        <Outlet />
      </div>
    </div>
  );
}

export default Layout;
