import React,{useState,useEffect} from "react";
import Sidebar from "./sidebar";
import { Outlet } from "react-router-dom";
import Search from "./search";

function Main() {
 const [search, setSearch] = useState(false)
  const [movies, setMovies] = useState([])
  const [searchTerm, setSearchTerm] = useState(''); // Store the search term

  const getmovies = async(query = "") => {

    let url = "";
    if (query) {
      // Use the search endpoint when a search query exists
      url = `https://api.themoviedb.org/3/search/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75&query=${query}`;
    } else {
      // Use the discover endpoint when there's no search query
      url = "https://api.themoviedb.org/3/discover/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75";
    }

    try {
      const response = await fetch(url);
      const data = await response.json();
      setMovies(data.results);
    } catch (err) {
      console.error(err);
    }

    // try{
    //   const response = await fetch(
    //     "https://api.themoviedb.org/3/discover/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75"
    //   );
    //   const data = await response.json();
    //   setMovies(data.results);
    //   // setMoviesProp(data.results);
    // }catch(err){
    //   console.error(err)
    // }
}

useEffect(()=>{
    getmovies();
},[])

  return (
    <div className="bg-black w-full h-screen">
    <div className="flex">
      <div className="w-1/12 bg-black">
        <Sidebar setSearch={setSearch} search={search}/>
        {search && <Search searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} 
      </div>
      <div className="w-11/12 bg-black">
        {/* Outlet will render the matched child route */}
        <Outlet context={[movies]} search={search}/>

      </div>
    </div>
  </div> 


  );
}

export default Main;
