import * as React from "react";
import PropTypes from "prop-types";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogTitle from "@mui/material/DialogTitle";
import { createTheme } from "@mui/material/styles";
import Profile from "@mui/icons-material/AccountCircle";
import Search from "@mui/icons-material/Search";
import Home from "@mui/icons-material/Home";
import Movies from "@mui/icons-material/Movie";
import { AppProvider } from "@toolpad/core/AppProvider";
import { DashboardLayout } from "@toolpad/core/DashboardLayout";
import Signin from "./signin";
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import { styled } from '@mui/material/styles';

// Navigation items for the sidebar and navbar
const NAVIGATION = [
  {
    segment: "dashboard",
    title: "My Space",
    path: "/dashboard",
    icon: <Profile />,
  },
  {
    segment: "search",
    title: "Search",
    icon: <Search />,
  },
  {
    segment: "home",
    title: "Home",
    icon: <Home />,
  },
  {
    segment: "movies",
    title: "Movies",
    icon: <Movies />,
  },
];

// Theme settings
const demoTheme = createTheme({
  cssVariables: {
    colorSchemeSelector: "data-toolpad-color-scheme",
  },
  colorSchemes: { light: true, dark: true },
  breakpoints: {
    values: {
      xs: 0,
      sm: 600,
      md: 600,
      lg: 1200,
      xl: 1536,
    },
  },
});

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': {
    padding: theme.spacing(2),
  },
  '& .MuiDialogActions-root': {
    padding: theme.spacing(1),
  },
}));

// Page content changes based on the current route (pathname)
function DemoPageContent({ pathname, onLoginClick }) {
  if (pathname === "/dashboard") {
    return (
      <Box
        sx={{
          backgroundColor: "bg-blue",
          height: "50vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          color: "white",
          textAlign: "center",
        }}
      >
        <Box>
          <Typography variant="h4" gutterBottom>
            Login to Disney+ Hotstar
          </Typography>
          <Typography variant="body1" gutterBottom>
            Start watching from where you left off, personalized for kids, and
            more
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={onLoginClick}
            sx={{
              width: "18rem", // Equivalent to 'w-72' in Tailwind
              fontWeight: "bold",
              py: 2, // Padding Y-axis
              px: 4, // Padding X-axis
              mt: 6, // Margin top
              backgroundColor: "#095ae5",
              "&:hover": {
                backgroundColor: "darkblue",
              },
            }}
          >
            Login
          </Button>

          {/* <p>Start watching from where you left off, persnaise for kids and more</p> */}
          {/* <Button className=" bg-blue-700 w-72 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-60" onClick={onLoginClick}>Login</Button> */}
          {/* <Button variant="contained" color="primary" onClick={onLoginClick} className=" bg-blue w-72 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-96" >
            Login
          </Button> */}
        </Box>
      </Box>
    );
  }

  // Add content for other pages
  switch (pathname) {
    case "/search":
      return (
        <Box>
          <Typography>Search Page</Typography>
        </Box>
      );
    case "/home":
      return (
        <Box>
          <Typography>Home Page</Typography>
        </Box>
      );
    case "/movies":
      return (
        <Box>
          <Typography>Movies Page</Typography>
        </Box>
      );
    default:
      return (
        <Box>
          <Typography>404: Page Not Found</Typography>
        </Box>
      );
  }
}

DemoPageContent.propTypes = {
  pathname: PropTypes.string.isRequired,
  onLoginClick: PropTypes.func.isRequired,
};

// Main layout component for the dashboard, navbar, and sidebar
function DashboardLayoutBasic(props) {
  const { window } = props;
  const [pathname, setPathname] = React.useState("/dashboard");
  const [open, setOpen] = React.useState(false);

  // Handle login button click to open dialog
  const handleClickOpen = () => {
    setOpen(true);
  };

  // Handle closing the dialog
  const handleClose = () => {
    setOpen(false);
  };

  // Router-like navigation handling
  const router = React.useMemo(() => {
    return {
      pathname,
      searchParams: new URLSearchParams(),
      navigate: (path) => setPathname(String(path)),
    };
  }, [pathname]);

  // Remove this const when copying and pasting into your project.
  const demoWindow = window !== undefined ? window() : undefined;

  return (
    <AppProvider
      navigation={NAVIGATION}
      router={router}
      theme={demoTheme}
      window={demoWindow}
    >
      <DashboardLayout>
        {/* Render different page content depending on the current path */}
        <DemoPageContent pathname={pathname} onLoginClick={handleClickOpen} />
        {/* Sign-in Popup Modal */}
        {/* <Dialog open={open} onClose={handleClose}  >
          <DialogContent>
            <Signin />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Close
            </Button>
          </DialogActions>
        </Dialog> */}

         {/* Sign-in Popup Modal */}
      <BootstrapDialog
        onClose={handleClose}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <DialogTitle id="customized-dialog-title">
         SignIn
        </DialogTitle>
        <IconButton
          aria-label="close"
          onClick={handleClose}
          sx={(theme) => ({
            position: 'absolute',
            right: 8,
            top: 8,
            color: theme.palette.grey[500],
          })}
        >
