  return (
    <>
    {/* (https://image.tmdb.org/t/p/w500${props?.poster_path}[0]) */}
      {/* <div style={{ backgroundImage: `linear-gradient(to left,rgba(0,0,0,7),rgba(0,0,0,0.1)),url(${img})`, height: "100vh", color: "white", backgroundRepeat:"no-repet" }}> */}

