import React, { useState } from "react";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
// import signin from "../image/signin.jpg";
import signin from "../image/qr.png";
import { useNavigate } from "react-router-dom";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import Typography from '@mui/material/Typography';
import Grid from "@mui/material/Grid";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 800,
  bgcolor: "background.paper",
  // bgcolor : grey,
  boxShadow: 24,
  p: 4,
  borderRadius: "8px",
};

function Signin({ open, handleClose }) {
  const [value, setValue] = useState("");
  const [otp, setOtp] = useState(""); // OTP state
  const [isOtpSent, setIsOtpSent] = useState(false); // To track if OTP has been sent
  const navigate = useNavigate();

  // Function to simulate sending OTP
  const handleSendOtp = () => {
    if (value) {
      alert(`OTP sent to ${value}. Use 1234 to verify.`);
      setIsOtpSent(true); // Set flag to indicate OTP has been sent
    } else {
      alert("Please enter a valid phone number.");
    }
  };

  // Function to handle OTP submission
  const handleVerifyOtp = () => {
    if (otp === "1234") {
      alert("OTP verified successfully!");
      navigate("/");
    } else {
      alert("Invalid OTP. Please try again.");
    }
  };

  return (
    <>
          <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-signin-title"
        aria-describedby="modal-signin-description"
      >
        <Box sx={style}>
          <Typography variant="h4" align="center" sx={{ mb: 2 }}>
            Login or Sign Up to Continue
          </Typography>
          <Typography align="center" sx={{ mb: 4 }}>
            Scan the QR code or enter your phone number to login
          </Typography>

          <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' } }}>
            {/* Left Side: Image */}
            <Box sx={{ flex: 1, textAlign: 'center', p: 2 }}>
              <img
                src={signin}
                alt="Signin"
                style={{
                  maxWidth: "100%",
                  height: "auto",
                  borderRadius: "12px",
                }}
              />
              <Typography variant="h6" sx={{ mt: 2 }}>
                Use your camera app to scan the QR
              </Typography>
              <Typography>
                Click the link generated to redirect to the Disney+ Hotstar mobile app
              </Typography>
            </Box>

            {/* Right Side: Form */}
            <Box sx={{ flex: 1, p: 2 }}>
              <Typography sx={{ mb: 2 }}>
                Enter your phone number to login
              </Typography>

              <PhoneInput
                className="mt-4 w-64 mb-3"
                defaultCountry="IN"
                placeholder="Enter phone number"
                value={value}
                onChange={setValue}
                style={{ width: '100%' }}
              />

              <Typography sx={{ mt: 2, mb: 2, color: "gray", fontSize: "0.875rem" }}>
                By proceeding, you confirm that you are above 18 years of age and
                agree to the{" "}
                <span style={{ color: "blue" }}>Privacy Policy & Terms of Use</span>.
              </Typography>

              <Button
                variant="contained"
                color="primary"
                sx={{ width: '100%' }}
                onClick={handleSendOtp}
              >
                Get OTP
              </Button>

              {isOtpSent && (
                <Box sx={{ mt: 4 }}>
                  <Typography>Enter OTP:</Typography>
                  <input
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    style={{ width: "100%", padding: "8px", marginTop: "8px" }}
                    placeholder="Enter OTP"
                  />
                  <Button
                    variant="contained"
                    color="success"
                    sx={{ mt: 2, width: '100%' }}
                    onClick={handleVerifyOtp}
                  >
                    Verify OTP
                  </Button>
                </Box>
              )}
            </Box>
          </Box>
        </Box>
      </Modal>
    </>
  );
}

export default Signin;
