import React from "react";
import { useLocation } from "react-router-dom";


function Home (){
  const location = useLocation();
  const movies = location.state?.movies || []; // Use optional chaining and default empty array

    return (
<>
<h1 className="text-slate-300 pl-11 font-bold text-xl">Latest Releses</h1>
<div className="grid grid-cols-6 bg-black">
{movies.map((data)=>{
    return  <>
    <div className=" rounded overflow-hidden shadow-lg mt-4 ml-4">
  <img className="w-full" src={`https://image.tmdb.org/t/p/w500${data.poster_path}`} alt="Sunset in the mountains"/>
</div>
</>
  })}
</div>
</>
)
}

export default Home;
