import React,{useState,useEffect} from "react";
import Sidebar from "./sidebar";
import { Outlet } from "react-router-dom";
import Welcome from "./welcome";
import Search from './search'

function Main() {
  const [movies, setMovies] = useState([])

  const getmovies = async(query = "") => {
    try{
      // const response = await fetch(
      //   "https://api.themoviedb.org/3/discover/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75"
      // );

      const url = query
      ? `https://api.themoviedb.org/3/search/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75&query=${query}`
      : `https://api.themoviedb.org/3/discover/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75`;

      const response = await fetch(url);
      const data = await response.json();
      setMovies(data.results);
      // setMoviesProp(data.results);
    }catch(err){
      console.error(err)
    }
}

useEffect(()=>{
    getmovies();
},[])

  // Handle search event
  const handleSearch = (query) => {
    getmovies(query); // Fetch movies based on the search query
  };

  return (
    <div className="bg-black w-full h-screen">
    <div className="flex">
      <div className="w-1/12 bg-black">
        <Sidebar />
      </div>
      <div className="w-11/12 bg-black">
        {/* Outlet will render the matched child route */}
        <Search onSearch={handleSearch} />
        <Outlet context={[movies]}/>

      </div>
    </div>
  </div> 


  );
}

export default Main;
