import React, { useEffect, useState } from "react";
import Sidebar from "./sidebar";
import { Outlet,useNavigate } from "react-router-dom";
// import Home from "./home";

function Main() {
  const [movies, setMovies] = useState([])
  const navigate = useNavigate();

    const getmovies = async() => {
        try{
      const response = await fetch(
        "https://api.themoviedb.org/3/discover/movie?api_key=b16e3ccef134709edd7ba814d4e2fd75"
      );
      const data = await response.json();
      setMovies(data.results);
      // Set the first movie poster path
      // if (data.results && data.results.length > 0) {
      //   setPoster(data.results[15].poster_path);
      // }
 // Navigate to home and pass movies through state
 navigate("/home", { state: { movies: data.results } });

        }catch(err){
          console.error(err)
        }
    }
    console.log(movies)

    useEffect(()=>{
        getmovies();
    },[])

  return (
    <div className="bg-black w-full h-screen">
    <div className="flex">
      <div className="w-1/12 bg-black">
        <Sidebar />
      </div>
      <div className="w-11/12 bg-black">
        {/* Outlet will render the matched child route */}
        <Outlet movies={movies} />
      </div>
    </div>
  </div> 
  );
}

export default Main;
