import React from "react";
import img from '../image/Bigg_Boss.png';
import Home from "./home";
// import { useOutletContext } from "react-router-dom";

function Welcome({search,movies}) {
  // const [movies] = useOutletContext();  // This should always give an array now
  // const movies = movies;
  console.log(movies);

  return (
    <>
 {search && <input type="search" className="block w-full p-4 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 " placeholder="movies, shows and more" required />}

      <div style={{backgroundImage:`linear-gradient(to right,rgba(0,0,0,7),rgba(0,0,0,0.1)),url(https://image.tmdb.org/t/p/w500${movies?.poster_path})`,
        backgroundRepeat:"no-repeat",backgroundSize:"cover"}} className='pb-7 h-screen grid grid-cols-3'>
        <div style={{ textAlign: "left", padding: "50px", marginTop : "50%"}}>
          <h1 className="text-slate-300 pt-60 font-bold text-4xl">{movies?.title}</h1>
          <h1 className="text-slate-300 mt-3">{movies?.release_date}</h1>
          <h1 className="text-slate-300 mt-4">{movies?.overview}</h1>
          <button className="bg-gray-500 mt-10 w-60 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded">
            Watch Now
          </button>
        </div>
      </div>
      <Home search={search}/>
    </>
  );
}

export default Welcome;
