import React, { useState } from "react";
import { Link } from "react-router-dom";
import ProfileOutlinedIcon from "@mui/icons-material/PersonOutline";
import ProfileFilledIcon from "@mui/icons-material/Person";
import SearchOutlinedIcon from "@mui/icons-material/SearchOutlined";
import SearchFilledIcon from "@mui/icons-material/Search";
import HomeOutlinedIcon from "@mui/icons-material/HomeOutlined";
import HomeFilledIcon from "@mui/icons-material/Home";
import MoviesOutlinedIcon from "@mui/icons-material/MovieOutlined";
import MoviesFilledIcon from "@mui/icons-material/Movie";
import LiveTvOutlinedIcon from "@mui/icons-material/LiveTvOutlined";
import LiveTvFilledIcon from "@mui/icons-material/LiveTv";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import Fade from "@mui/material/Fade";

const Sidebar = () => {
  const [touch, setTouch] = useState(false);
  const [hoveredIcon, setHoveredIcon] = useState(null);

  const handleMouseEnter = (iconName) => {
    setHoveredIcon(iconName);
    setTouch(true);
  };

  const handleMouseLeave = () => {
    setHoveredIcon(null);
    setTouch(false);
  };

  return (
    <div className="z-20 fixed grid grid-cols-2 bg-black h-full w-28">
      <div onMouseEnter={() => setTouch(true)} onMouseLeave={() => setTouch(false)}>
        <img src={hostaicon} className="ml-7 mt-5 w-40" />
        <div className="w-64 ml-5 mt-1 ">
          <span className="rounded-xl text-center" style={{ backgroundColor: "rgb(74, 62, 30, 1)" }}>
            <span className="text-amber-200 ml-2 text-xs">
              Subscribe <ArrowForwardIosIcon sx={{ width: "0.7rem", marginRight: "4px" }} />
            </span>
          </span>
        </div>

        <div className="mt-52">
          <Link to="/login">
            {hoveredIcon === "profile" ? (
              <ProfileFilledIcon className="w-5 ml-9 mt-12 cursor-pointer text-slate-300" />
            ) : (
              <ProfileOutlinedIcon
                className="w-5 ml-9 mt-12 cursor-pointer text-slate-300 hover:text-gray-100 hover:bg-gray-700"
                onMouseEnter={() => handleMouseEnter("profile")}
                onMouseLeave={handleMouseLeave}
              />
            )}
          </Link>

          <Link to="/search">
            {hoveredIcon === "search" ? (
              <SearchFilledIcon className="w-5 ml-9 mt-12 cursor-pointer text-slate-300" />
            ) : (
              <SearchOutlinedIcon
                className="w-5 ml-9 mt-12 cursor-pointer text-slate-300 hover:text-gray-100 hover:bg-gray-700"
                onMouseEnter={() => handleMouseEnter("search")}
                onMouseLeave={handleMouseLeave}
              />
            )}
          </Link>

          <Link to="/welcome">
            {hoveredIcon === "home" ? (
              <HomeFilledIcon className="w-5 ml-9 mt-12 cursor-pointer text-slate-300" />
            ) : (
              <HomeOutlinedIcon
                className="w-5 ml-9 mt-12 cursor-pointer text-slate-300 hover:text-gray-100 hover:bg-gray-700"
                onMouseEnter={() => handleMouseEnter("home")}
                onMouseLeave={handleMouseLeave}
              />
            )}
          </Link>

          <Link to="/movie">
            {hoveredIcon === "movie" ? (
              <MoviesFilledIcon className="w-5 ml-9 mt-12 cursor-pointer text-slate-300" />
            ) : (
              <MoviesOutlinedIcon
                className="w-5 ml-9 mt-12 cursor-pointer text-slate-300 hover:text-gray-100 hover:bg-gray-700"
                onMouseEnter={() => handleMouseEnter("movie")}
                onMouseLeave={handleMouseLeave}
              />
            )}
          </Link>

          <Link to="/tv">
            {hoveredIcon === "tv" ? (
              <LiveTvFilledIcon className="w-5 ml-9 mt-12 cursor-pointer text-slate-300" />
            ) : (
              <LiveTvOutlinedIcon
                className="w-5 ml-9 mt-12 cursor-pointer text-slate-300 hover:text-gray-100 hover:bg-gray-700"
                onMouseEnter={() => handleMouseEnter("tv")}
                onMouseLeave={handleMouseLeave}
              />
            )}
          </Link>
        </div>
      </div>

      {touch && (
        <Fade in={touch}>
          <div className="text-slate-100 bg-black-900 bg-opacity-60 ml-8 w-20 h-screen font-bold text-base">
            <h4 style={{ marginTop: "37.1vh" }} className="hover:font-bold hover:text-gray-400">
              My space
            </h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">Search</h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">Home</h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">Movie</h4>
            <h4 className="mt-12 hover:font-bold hover:text-gray-100">TV</h4>
          </div>
        </Fade>
      )}
    </div>
  );
};

export default Sidebar;








