import React,{useState,useEffect} from "react";
import Sidebar from "./sidebar";
import Home from "./home";
import Welcome from "./welcome";


function Main() {
 const [search, setSearch] = useState(false)
  const [movies, setMovies] = useState([])
  const [menu,setMenu] = useState('')


  const getmovies = async(query = "") => {


    try{
      const response =
      await fetch(menu === "home" || menu === "" ? `https://api.themoviedb.org/3/movie/upcoming?api_key=b16e3ccef134709edd7ba814d4e2fd75` : `https://api.themoviedb.org/3/discover/${menu ? menu : "movie"}?api_key=b16e3ccef134709edd7ba814d4e2fd75`)
      const data = await response.json();
      setMovies(data.results);
    }catch(err){
      console.error(err)
    }
}

useEffect(()=>{
    getmovies();
},[menu])

  return (
    <div className="bg-black h-screen min-h-screen">
    <div className="flex">
      <div className="w-1/12 bg-black">
        <Sidebar setSearch={setSearch} search={search} setMenu={setMenu}/>
      </div>
      {!search && <div className="w-11/12 bg-black">
       <Welcome movies={movies[10]}/>
      </div>}
    </div>
    <div className="bg-black">
        <Home search={search} movies={movies}/>
      </div>
  </div> 


  );
}

export default Main;
