import React, { useState } from "react";

function Home({ search, movies }) {
  const [searchkeys, setSearchKeys] = useState("");

  // Check if movies is defined and is an array
  // if (!movies || !Array.isArray(movies)) {
  //   return <div className="text-white">No movies available.</div>;
  // }

  return (
    <>
      {search && (
        <input
          onChange={(e) => setSearchKeys(e.target.value)}
          type="text"
          className="ml-28  bg-gray-800 border 
         border-gray-800 text-white text-sm rounded-lg focus:ring-gray-800
          focus:border-gray-800 block w-10/12 p-2.5 outline-none"
          placeholder="Movies, Shows and more"
          required
        />
      )}
      <div className="grid grid-cols-6 bg-black ">
        {movies
          ?.filter((data) => data.title || data.name.includes(searchkeys))
          .map((data) => {
            return (
              <>
                <div
                  key={data.id}
                  className=" rounded overflow-hidden shadow-lg mt-4 ml-4"
                >
                  <img
                    className="w-full"
                    src={`https://image.tmdb.org/t/p/w500${data.poster_path}`}
                    alt="Sunset in the mountains"
                  />
                </div>
              </>
            );
          })}
      </div>
    </>
  );
}

export default Home;
